        for (int j = 0; j < Cb; j++) {
            C[i][j] = 0;
            for (int k = 0; k < Ca; k++) {
                C[i][j] += A[i][k] * B[k][j];
            }
        }
    }

    printf("%d %d\n", Ra, Cb);
    for (int i = 0; i < Ra; i++) {
        for (int j = 0; j < Cb; j++) {
