### N个有理数求和

``` c++
#include <stdio.h>
#include <stdlib.h>

#define ll long long

ll abss(ll x) { return x < 0 ? -x : x; }

ll gcd(ll a, ll b) {
    if (a < 0) a = -a;
    if (b < 0) b = -b;
    while (b != 0) {
        ll t = a % b;
        a = b;
        b = t;
    }
    return a;
}

int main() {
    int n;
    if (scanf("%d", &n) != 1) return 0;

    ll num = 0;
    ll den = 1;

    for (int i = 0; i < n; ++i) {
        ll a, b;
        scanf("%lld/%lld", &a, &b);

        ll g = gcd(den, b);
        ll den1 = den / g;
        ll b1 = b / g;

        ll new_num = num * b1 + a * den1;
        ll new_den = den * b1;

        ll g2 = gcd(abss(new_num), new_den);
        new_num /= g2;
        new_den /= g2;

        if (new_den < 0) { new_den = -new_den; new_num = -new_num; }

        num = new_num;
        den = new_den;
    }

    ll integer = num / den;
    ll remainder = num % den;

    if (remainder == 0) {
        printf("%lld", integer);
        putchar('\n');
    } else {
        if (integer != 0) {
            printf("%lld", integer);
            putchar(' ');
            printf("%lld", remainder);
            putchar('/');
            printf("%lld", den);
            putchar('\n');
        } else {
            printf("%lld", remainder);
            putchar('/');
            printf("%lld", den);
            putchar('\n');
        }
    }

    return 0;
}

